import 'package:flutter/material.dart';

void main() {
  runApp(const PlayersApp());
}

class PlayersApp extends StatefulWidget {
  const PlayersApp({super.key});

  @override
  State<PlayersApp> createState() => _PlayersAppState();
}

class _PlayersAppState extends State<PlayersApp> {
  int _counter = 0;
  List<String> playerList = <String>[
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQn4KvLYWgA2edhOvqRWp_vRsbXjNE_Rjdhgw0KDmmksyGKVO5fmCvoqfE90cVFzRyGanQ&usqp=CAU",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTkTk_wXuJMA9Z5pBAkSOp0q-LjfAh9sliWys2kTuViMbLL0X_VRNO4z2hCjVJOLMrybxc&usqp=CAU",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQF3o1G6tH6tUNtHthPDhR1upwirL2KNJxrZg&s",
    "data:image/jpeg;base64,",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS1J3yngoOj-PpF0VmitILMfl0DcS6JrEFXJQ&s"
  ];
   @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Players',
      home: Scaffold(
        appBar: AppBar(
          backgroundColor: Colors.blue,
          title: const Text("Players App"),
          centerTitle: true,
        ),
        body: Center(
          child: Column(
            mainAxisAlignment: MainAxisAlignment.center,
            children: <Widget>[
              Image.network(
                playerList[_counter],
                height: 300,
              ),
            ],
          ),
        ),
        floatingActionButton: FloatingActionButton(onPressed: () {
          if (_counter < playerList.length - 1) {
            _counter++;
          } else {
            _counter = 0;
          }
          setState(() {});
        },
        tooltip: 'Increment',
        child: const Icon(Icons.add),
        ),
      ),
    );
  }
}
